import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import signupImage from './interviewsignup.jpg';
import './signup.css';


const Signup = () => {
  const [form, setForm] = useState({ name: '', email: '', password: '', skills: '' });
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/auth/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form)
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.msg);
      localStorage.setItem('token', data.token);
      navigate('/');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="signup-container">
      <header className="signup-header">"Sharpen your skills with prep4Sde! ðŸš€"</header>
      <div className="signup-main">
        <div className="signup-image">
          <img src={signupImage} alt="Signup Visual" />
        </div>
        <div className="signup-form-container">
          <h1 className="signup-title">prep4Sde</h1>
          <form className="signup-form" onSubmit={handleSubmit}>
            <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
            <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
            <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
            <input type="text" name="skills" placeholder="Skills (comma separated)" onChange={handleChange} required />
            {error && <p style={{ color: 'red', fontSize: '13px' }}>{error}</p>}
            <button type="submit">Sign Up</button>
          </form>
          <p style={{ marginTop: '10px', fontSize: '14px' }}>
            Already have an account? <a href="/login">Login</a>
          </p>
        </div>
      </div>
      <footer className="signup-footer">
        <a href="#">Privacy Policy</a>
        <a href="#">Terms</a>
        <a href="#">About</a>
        <a href="#">Help</a>
        <a href="#">Contact</a>
        <p style={{ marginTop: '5px' }}>Â© {new Date().getFullYear()} prep4Sde</p>
      </footer>
    </div>
  );
};

export default Signup;
